-- Mostrar los 10 productos mas vendidos en la historia y también los 10 productos 
-- menos vendidos en la historia. Además mostrar de esos productos, quien fue el 
-- cliente que mayor compra realizo. SELECT	prod_codigo,	(SELECT TOP 1 fact_cliente FROM dbo.Item_Factura INNER JOIN dbo.Factura ON item_tipo = fact_tipo AND item_sucursal = fact_sucursal AND item_numero = fact_numero WHERE item_producto = prod_codigo ORDER BY item_cantidad * item_precio DESC)FROM dbo.ProductoWHERE 	prod_codigo IN (	SELECT TOP 10 item_producto 	FROM dbo.Item_Factura	GROUP BY item_producto	ORDER BY SUM(item_cantidad) DESC	)OR	prod_codigo IN (	SELECT TOP 10 item_producto	FROM dbo.Item_Factura	GROUP BY item_producto	ORDER BY SUM(item_cantidad) ASC	)