-- Hacer una función que dado un articulo y un deposito devuelva un string que 
-- indique el estado del deposito según el articulo. Si la cantidad almacenada es menor 
-- al limite retornar “OCUPACION DEL DEPOSITO XX %” siendo XX el % de 
-- ocupación. Si la cantidad almacenada es mayor o igual al límite retornar -- "DEPOSITO COMPLETO"USE [Guia de ejerecicios]IF EXISTS (SELECT * FROM sysobjects WHERE name='dbo.mi_funcion') BEGINDROP FUNCTION dbo.mi_funcionENDCREATE FUNCTION dbo.mi_funcion(	@prod_codigo char(8),	@depo_codigo char(2))RETURNS varchar(50)BEGIN	DECLARE @stock_actual decimal(12,2), @stock_maximo decimal(12,2)		SELECT @stock_actual = stoc_cantidad, @stock_maximo = stoc_stock_maximo		FROM dbo.STOCK		WHERE stoc_producto = @prod_codigo			AND stoc_deposito = @depo_codigo		IF (@stock_maximo < @stock_actual)		RETURN 'DEPOSITO COMPLETO'			RETURN 'OCUPACION DEL DEPOSITO ' + CAST(@stock_actual * 100 / @stock_maximo AS varchar) + '%'ENDSELECT dbo.mi_funcion('00000030', '00')